function check_settings(const)% check experimental setup% like distance between chin rest and screen etc.%% monitor distanceButtonName = questdlg('Bildschirmabstand 2.10m?','','Ja','Nein','Ja');if strcmp(ButtonName,'Nein')    error('Falscher Bildschirmabstand');end% soundButtonName = questdlg('Ist der Ton angeschaltet?','','Ja','Nein','Ja');if strcmp(ButtonName,'Nein')    error('Vor der Testung Boxen anschalten!');end% lightButtonName = questdlg('Ist der Raum abgedunkelt und die Tischlampe aus?','','Ja','Nein','Ja');if strcmp(ButtonName,'Nein')    error('Vor der Testung bitte abdunkeln!');end% existing file name nameOut=['data/' const.code '.mat'];if exist(nameOut,'file')    questAns=questdlg('Die Datei besteht bereits! Wollen Sie diese Versuchsperson erneut testen?','','Ja','Nein','Nein');    if strcmp(questAns,'Nein')        error('Experiment abgebrochen, Versuchsperson existiert bereits!!!');    endend